const h=(t,r=2,e=",",g=".")=>{try{r=Math.abs(r),r=isNaN(r)?2:r;const a=t<0?"-":"";let s=parseInt(t=Math.abs(Number(t)||0).toFixed(r)).toString(),b=s.length>3?s.length%3:0;return a+(b?s.substr(0,b)+g:"")+s.substr(b).replace(/(\d{3})(?=\d)/g,"$1"+g)+(r?e+Math.abs(t-parseFloat(s)).toFixed(r).slice(2):"")}catch(a){console.log(a)}},o=(t,r)=>{const e=Math.pow(10,r);return Math.round(t*e)/e};export{h as f,o as r};
